import kotlin.test.*
import com.lucaspowered.bfk.Interpreter
import java.io.ByteArrayOutputStream

class InterpreterTests {
    private val outputStream = ByteArrayOutputStream()
    private val interpreter = Interpreter(stream = outputStream)

    private fun testInterpreter(input: String, expected: String) {
        interpreter.run(input)

        assertEquals(expected, outputStream.toString())
        outputStream.reset()
    }

    @Test
    fun `char a`() =
        testInterpreter("+++++++++++++++++++++++++++++++++++++++++++++" +
            "++++++++++++++++++++++++++++++++++++++++++++++++++++.", "a")

    @Test
    fun `Hello World`() =
        testInterpreter(">+++++++++[<++++++++>-]<.>++++++[<+++++>-]<-.+++++++..+++.>>++" +
                "+++++[<++++++>-]<++.------------.<++++++++.--------.+++.------.--------.>+.>++++++++++.", "Hello, world!\n")


    @Test
    fun `Nested Loop Hello World`() =
        testInterpreter("+[-[<<[+[--->]-[<<<]]]>>>-]>-.---.>..>.<<<<-.<+.>>>>>.>.<<.<-.", "hello world")


